import React, { useState } from 'react';

export default function Number() {
    const [formattedNumber, setFormattedNumber] = useState('');

    const generateNumber = () => {
        // Generate a random 10-digit number
        const randomNumber = Math.floor(1000000000 + Math.random() * 9000000000).toString();
        // Format it as 3-3-4
        const formatted = `${randomNumber.slice(0, 3)} ${randomNumber.slice(3, 6)} ${randomNumber.slice(6)}`;
        setFormattedNumber(formatted);
    };

    return (
        <>
            <div className="flex flex-col   h-screen bg-gray-100">
                <h1 className='text-3xl font-semibold mb-5'>The Major System: Memory Technique For Numbers</h1>
                <p className='text-gray800/90'>What is a major system in memory? How does it work, and can it really help you memorize numbers?

                </p>
                <p>The major system is a technique where you memorize sequences of numbers by turning the number into a word, and the word into a place. Many top mnemonists use the major system of memory for events and tasks involving cards.

                </p>
                <h1 className="text-xl  mb-4">Generate a 10-Digit Number</h1>
                <button
                    onClick={generateNumber}
                    className="bg-blue-500 w-48 text-white px-4 py-2 rounded-lg hover:bg-blue-600 transition-colors mb-4"
                >
                    Generate Number
                </button>
                {formattedNumber && (
                    <div className="border border-gray-300 rounded-lg p-4 text-xl">
                        {formattedNumber}
                    </div>
                )}
            </div>
        </>
    );
}
